    private static Map<String, String> map = new HashMap<>();

    public static String getMimeTypeByExtension(String extension)
    {
        String mineType = MimeType.map.get(extension);
        return mineType;
    }

    public static void parse()
    {
        try
        {
            JSONObject object = new JSONObject(MIME_TYPE);

            Iterator<String> iterator = object.keys();
            while ( iterator.hasNext() )
            {
                String key = iterator.next();
                String value = object.getString(key);
                MimeType.map.put(key, value);
            }
        }
        catch (JSONException e)
        {
            e.printStackTrace();
        }

    }


    // ----------------------- BY EXTENSION ----------------------- //
    public static boolean isImageByExtension(String extension)
    {
        extension = extension.toLowerCase();
        return MimeType.isInThisTypeByExtension(PIC_END, extension);
    }

    public static boolean isVideoByExtension(String extension)
    {
        extension = extension.toLowerCase();
        return MimeType.isInThisTypeByExtension(VIDEO_END, extension);
    }


    private static final String[] PIC_END = new String[]
            {
                    ".jpeg", ".jpg", ".ico", ".tif", ".pcx", ".tga", ".png", ".bmp", ".gif"
            };

    private static final String[] VIDEO_END = new String[]
            {
                    ".swf", ".vob", ".mkv", ".mov", ".mp4", ".3gp", ".avi", ".rm", ".rmvb", ".wmv", ".flv", ".asf", ".m4v"
            };

    private static boolean isInThisTypeByExtension(String[] typeArray, String filename)
    {
        filename = filename.toLowerCase(); // to lower case

        for (String extantion : typeArray)
        {
            if (filename.endsWith(extantion))
            {
                return true;
            }
        }
        return false;
    }


    // ----------------------- BY MIMETYPE ----------------------- //
    public static boolean isImageByMimeType(String mimeType)
    {
        return mimeType.toLowerCase().contains("image");
    }

    public static boolean isVideoByMimeType(String mimeType)
    {
        return mimeType.toLowerCase().contains("video");
    }

    public static boolean isTextByMimeType(String mimeType)
    {
        return mimeType.toLowerCase().startsWith("text");
    }

    public static boolean isSystemByMimeType(String mimeType)
    {
        return mimeType.toLowerCase().equals("application/diuit-chat-sys-message");
    }
